import React,{useState} from "react"

const Calculator = () => {
    const [angka,setAngka] = useState({
        angka1:'',
        angka2:''
    })
       
    const handleAngka1InputChange = (event) => {
        event.persist();
        setAngka((angka) => ({
            ...angka,
            angka1: event.target.value,
        }));
    }

    const handleAngka2InputChange = (event) => {
        event.persist();
        setAngka((angka) => ({
            ...angka,
            angka2: event.target.value,
        }));
    }

    const add=()=>{
        setAngka(angka.angka1+angka.angka2)
    }

    return (
      <div>
          <h1>Penjumlahan:{angka}</h1>
          <input type="text" placeholder="Enter number.."  value={angka.angka1} onChange={handleAngka1InputChange}/>+
          <input type="text" placeholder="Enter number.."  value={angka.angka2} onChange={handleAngka2InputChange}/>
          <button onClick={add}>Add</button>
         
      </div>
    )
};

export default Calculator;
