import React, { useRef, useState,useEffect } from "react"
const Example = () => {
  const [name, setName] = useState("")
  const inputRef = useRef()
  useEffect(() => {
    inputRef.current.focus()
  },[])
  return(
    <div>
      <input
       type="text"
       ref={inputRef}
       value={name}
       placeholer="name"
      />
    </div>
  )
}
export default Example
