  useEffect(() => {
    inputRef.current.focus()
   
  },[])
  return(
    <div>
    <h3>{name}</h3>
      <input
       type="text"
       ref={inputRef}
       value={name}
       placeholer="name"
      />
    </div>
  )
}
export default Example
