import React, { useEffect, useState } from "react"
import { useDispatch, useSelector } from "react-redux";
import { Link } from 'react-router-dom';
import { useNavigate } from "react-router-dom"
import { retrieveTutorials } from "../../Redux/Actions/tutorials";

const UserRedux = () => {
    let navigate = useNavigate()

    let users = useSelector(state => state.userReducer.users);
    const dispatch = useDispatch();


    useEffect(() => {
        dispatch(retrieveTutorials());
    }, [dispatch]);


    const editdata = (id) => {
        navigate('/edit', { state: { id } })
    }

    return (
        <div>
            <main id="main" class="main">

                <div class="pagetitle">
                    <h1>Dashboard</h1>
                    <nav>
                        <ol class="breadcrumb">
                            <li class="breadcrumb-item"><a href="index.html">Home</a></li>
                            <li class="breadcrumb-item active">Dashboard</li>
                        </ol>
                    </nav>
                </div>

                <section class="section dashboard">
                    <div class="row">


                        <div class="col-lg-12">
                            <div class="row">
                            <div class="col-lg-4">
                                <Link to='/adduser' className="btn btn-sm btn-primary mr-1" >Add User</Link>
                                </div>
                                {
                                    users && users.map((user, i) => {
                                        return (
                                            <>
                                                <h5>{i + 1} {user.username} {user.password} </h5>
                                                <div class="col-lg-4"><button className="btn btn-sm btn-primary mr-1" onClick={() => editdata(user.id)}>Edit User</button></div>
                                            </>
                                        )
                                    })

                                }

                            </div>
                        </div>



                    </div>

                </section>

            </main>





        </div>
    )
};

// export default productRedux



