import React,{useState} from "react"

const Calculator = (props) => {
    const [angka1,angka2,setAdd] = useState(0)

    const add=()=>{
        setAdd(angka1+angka2);
    };
    return (
      <div>
          <h1>Penjumlahan</h1>
          <input type="text" placeholder="Enter number.."  value={angka1}/>+
          <input type="text" placeholder="Enter number.."  value={angka2}/>
          <button onClick={add}>Add</button>
         
      </div>
    )
};

export default Calculator;
