import React, { useEffect, useState } from "react"
import { useDispatch, useSelector } from "react-redux";
import { Link } from 'react-router-dom';
import { useNavigate } from "react-router-dom"
import {retrieveTutorials} from "../Redux/Actions/tutorials";

const UserRedux = () => {
    let navigate = useNavigate()

    let users = useSelector(state => state.userReducer.users); 
    const dispatch = useDispatch(); 


    useEffect(() => {
        dispatch(retrieveTutorials());
    }, [dispatch]);


    const editdata = (id) => {
        navigate('/edit', { state: { id } })
    }

    return (
        <div>

            <Link to='/adduser' className="btn btn-sm btn-primary mr-1">Add User</Link>
            {
                users && users.map((user, i) => {
                    return (
                        <>
                            <h5>{i + 1} {user.username} {user.password} </h5>
                            <button onClick={() => editdata(user.id)}>Edit User</button>
                        </>
                    )
                })

            }




        </div>
    )
};

// export default productRedux



