import React,{useState} from "react"


const Operation = (props) => {
  
    //const [hasil, setHasil] = useState(0
        
    //)
    const [hasil, setHasil] = useState({
        total:'',
    })

    const add=( )=>{
        setHasil((previousState ) => ({  
            ...previousState,        
            total: parseInt(props.angka1)+parseInt(props.angka2)
        }))
    }
    const kurang=( )=>{
        setHasil((previousState ) => ({  
            ...previousState,        
            total: parseInt(props.angka1)-parseInt(props.angka2)
        }))
    }
    const kali=( )=>{
        setHasil((previousState ) => ({  
            ...previousState,        
            total: parseInt(props.angka1)*parseInt(props.angka2)
        }))
    }
    const bagi=( )=>{
        setHasil((previousState ) => ({  
            ...previousState,        
            total: parseInt(props.angka1)/parseInt(props.angka2)
        }))
    }
    return (
        <div>
         
            <h1>Penjumlahan:{hasil.total}</h1>
            <button onClick={add}>Add</button>
            <br/>
            <h1>Pengurangan:{hasil.total}</h1>
            <button onClick={kurang}>Kurang</button>
            <br/>
            <h1>Perkalian:{hasil.total}</h1>
            <button onClick={kali}>Kali</button>
            <br/>
            <h1>Pembagian:{hasil.total}</h1>
            <button onClick={bagi}>Bagi</button>


        </div>
    )
};

export default Operation;
