import React,{useState} from 'react'
 import axios from 'axios';
import ApiLogin from "../Api/apiLogin";
import { useNavigate } from "react-router-dom"

export default function SignIn() {
    const [errrorMessage, setErrorMessage] = useState('')
    const [dataApi, setData] = useState({
        username:'imelda',
        password:'password'
    })
    let navigate = useNavigate();
  
    const handleSubmit = async (event) => {
       
      event.preventDefault();
    //   const { data }=await ApiLogin.login(dataApi.username, dataApi.password)
      const { data } = await axios.post("http://localhost:3001/auth", dataApi);
      if (data.message === 'You have logged in') {
        //localStorage.setItem('token', data.token);
        //setIsLoggedIn(true)
        navigate('/home')       
        
      } else {
        alert(data)
      }
    }

    return(
        <main id="main" class="main">

      <div class="pagetitle">
        <h1>Dashboard</h1>
        <nav>
          <ol class="breadcrumb">
            <li class="breadcrumb-item"><a href="index.html">Home</a></li>
            <li class="breadcrumb-item active">Dashboard</li>
          </ol>
        </nav>
      </div>

      <section class="section dashboard">
        <div class="row">

          <center>
            <div class="col-lg-5">
              <div class="row">

                {/* <button  onClick={onSignIn}>Sign in</button> */}
                <div class="card mb-3">

                  <div class="card-body">

                    <div class="pt-4 pb-2">
                      <h5 class="card-title text-center pb-0 fs-4">Login to Your Account</h5>
                      <p class="text-center small">Enter your username & password to login</p>
                    </div>

                    <form >

                      <div class="col-12">
                        <label for="yourUsername" class="form-label">Username</label>
                        <div >

                          <input type="text" name="username" class="form-control" id="yourUsername" />
                          <div class="invalid-feedback">Please enter your username.</div>
                        </div>
                      </div>

                      <div class="col-12">
                        <label for="yourPassword" class="form-label">Password</label>
                        <input type="password" name="password" class="form-control" id="yourPassword" />
                        <div class="invalid-feedback">Please enter your password!</div>
                      </div>

                      <div class="col-12">
                        <div class="form-check">
                          <input class="form-check-input" type="checkbox" name="remember" value="true" id="rememberMe" />
                          <label class="form-check-label" for="rememberMe">Remember me</label>
                        </div>
                      </div>
                      <div class="col-12">
                        <button class="btn btn-primary w-100" onClick={handleSubmit}>Sign in</button>

                      </div>

                    </form>

                  </div>
                </div>



              </div>
            </div>


          </center>
        </div>

      </section>

    </main>
    )
};
