import React from "react"
import { connect } from "react-redux";
import { doAddProduct, doDeleteProduct, doUpdateProduct } from "../Redux/Actions/reducerAction";


const productRedux = (props) => {

    const addData = () => {
        const payload = {
            id: 6, nama: 'keyboard', stok: 9
        }
        props.onAddProduct(payload)
    }

    const editData = (id) => {
        const payload = {
            id: id, nama: 'keyboard', stok: 5
        }
        props.onEditProduct(payload)
    }

    const delData = (id) => {
        const payload = {
            id: id, nama: 'keyboard', stok: 5
        }
        props.onDelProduct(payload)
    }


    return (
        <div>
            {
                props.products && props.products.map(prod => {
                    return (
                        <>
                            <h5>{prod.id} {prod.nama} {prod.stok}</h5>
                        </>
                    )
                })

            }
            <button onClick={addData}> Add</button>
            <button onClick={() => editData(2)}> Edit</button>
            <button onClick={() => delData(2)}> Delete</button>



        </div>
    )
};

// export default productRedux

const mapStateToProps = (state) => ({
    products: state.prodReducers.data_product
})

const mapdispatchToProps = (dispatch) => {
    return {
        onAddProduct: payload => dispatch(doAddProduct(payload)),
        onEditProduct: payload => dispatch(doUpdateProduct(payload)),
        onDelProduct: payload => dispatch(doDeleteProduct(payload))
    }
}


export default connect(mapStateToProps, mapdispatchToProps)(productRedux)
